#!/bin/bash

wget --load-cookies /tmp/cookies.txt "https://drive.google.com/uc?export=download&confirm=$(wget --quiet --save-cookies /tmp/cookies.txt --keep-session-cookies --no-check-certificate 'https://drive.google.com/uc?export=download&id=1pn_ioHdeUzBcX7FBTP8S0f_Ebxpp1Hlf' -O- | sed -rn 's/.*confirm=([0-9A-Za-z_]+).*/\1\n/p')&id=1pn_ioHdeUzBcX7FBTP8S0f_Ebxpp1Hlf" -O model.pt && rm -rf /tmp/cookies.txt